SELECT MIN(k.keyword) AS movie_keyword, MIN(n.name) AS actor_name, MIN(t.title) AS hero_movie FROM postgres.cast_info AS ci, postgres.keyword AS k, postgres.movie_keyword AS mk, postgres.name AS n, postgres.title AS t WHERE k.keyword in ( 'mercedes-benz','insurance-company-rep','earring','wind-up-toy','canned-food','japanese-type-89-127-mm-antiaircraft-gun','fighting-brothers','stilleto','white-slavery','corset' ) AND n.name LIKE '%Downey%Robert%' AND t.production_year > 2009 AND k.id = mk.keyword_id AND t.id = mk.movie_id AND t.id = ci.movie_id AND ci.movie_id = mk.movie_id AND n.id = ci.person_id;