SELECT MIN(chn.name) AS character_alias, MIN(t.title) AS movie_with_american_producer FROM postgres.char_name AS chn, postgres.cast_info AS ci, postgres.company_name AS cn, postgres.company_type AS ct, postgres.movie_companies AS mc, postgres.role_type AS rt, postgres.title AS t WHERE ci.note like '%(as Arie%' AND cn.country_code = '[us]' AND t.production_year > 1966 AND t.id = mc.movie_id AND t.id = ci.movie_id AND ci.movie_id = mc.movie_id AND chn.id = ci.person_role_id AND rt.id = ci.role_id AND cn.id = mc.company_id AND ct.id = mc.company_type_id;