SELECT MIN(t.title) AS movie_title FROM postgres.keyword AS k, postgres.movie_info AS mi, postgres.movie_keyword AS mk, postgres.title AS t WHERE k.keyword like '%sequel%' AND mi.info IN ( 'USA:97','USA:4 May 2004','Documentary','4','Color','Mono','Poland:6 October 2006','UK:5 October 2012','Black and White' ) AND t.production_year > 1987 AND t.id = mi.movie_id AND t.id = mk.movie_id AND mk.movie_id = mi.movie_id AND k.id = mk.keyword_id;