SELECT MIN(cn.name) AS movie_company, MIN(mi_idx.info) AS rating, MIN(t.title) AS mainstream_movie FROM postgres.company_name AS cn, postgres.company_type AS ct, postgres.info_type AS it1, postgres.info_type AS it2, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.movie_info_idx AS mi_idx, postgres.title AS t WHERE cn.country_code = '[us]' AND ct.kind = 'distributors' AND it1.info = 'LD laserdisc title' AND mi_idx.info > '510' AND t.production_year between 1967 and 1981 AND t.id = mi.movie_id AND t.id = mi_idx.movie_id AND mi.info_type_id = it1.id AND mi_idx.info_type_id = it2.id AND t.id = mc.movie_id AND ct.id = mc.company_type_id AND cn.id = mc.company_id AND mc.movie_id = mi.movie_id AND mc.movie_id = mi_idx.movie_id AND mi.movie_id = mi_idx.movie_id;