SELECT MIN(n.name) AS voicing_actress, MIN(t.title) AS jap_engl_voiced_movie FROM postgres.aka_name AS an, postgres.char_name AS chn, postgres.cast_info AS ci, postgres.company_name AS cn, postgres.info_type AS it, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.name AS n, postgres.role_type AS rt, postgres.title AS t WHERE ci.note in ( '(motion actor)','(production assistant)','(production accountant)','(additional unit assistant)','(voice)' ) AND cn.country_code = '[us]' AND it.info = 'trivia' AND mi.info is not null and ( mi.info like '%%' or mi.info like '%%' ) AND n.gender = 'm' and n.name like '%or, Ri%' AND rt.role = 'actor' AND t.production_year > 1997 AND t.id = mi.movie_id AND t.id = mc.movie_id AND t.id = ci.movie_id AND mc.movie_id = ci.movie_id AND mc.movie_id = mi.movie_id AND mi.movie_id = ci.movie_id AND cn.id = mc.company_id AND it.id = mi.info_type_id AND n.id = ci.person_id AND rt.id = ci.role_id AND n.id = an.person_id AND ci.person_id = an.person_id AND chn.id = ci.person_role_id;