SELECT MIN(mi.info) AS movie_budget, MIN(mi_idx.info) AS movie_votes, MIN(n.name) AS writer, MIN(t.title) AS complete_gore_movie FROM postgres.complete_cast AS cc, postgres.comp_cast_type AS cct1, postgres.comp_cast_type AS cct2, postgres.cast_info AS ci, postgres.info_type AS it1, postgres.info_type AS it2, postgres.keyword AS k, postgres.movie_info AS mi, postgres.movie_info_idx AS mi_idx, postgres.movie_keyword AS mk, postgres.name AS n, postgres.title AS t WHERE cct1.kind in ('complete','crew','complete+verified','crew','complete+verified','complete+verified','complete+verified') AND cct2.kind = 'complete+verified' AND it1.info = 'goofs' AND it2.info = 'top 250 rank' AND n.gender = 'f' AND t.production_year > 1985 and ( t.title like '%ddy%' or t.title like '%%' or t.title like 'Saw%' ) AND t.id = mi.movie_id AND t.id = mi_idx.movie_id AND t.id = ci.movie_id AND t.id = mk.movie_id AND t.id = cc.movie_id AND ci.movie_id = mi.movie_id AND ci.movie_id = mi_idx.movie_id AND ci.movie_id = mk.movie_id AND ci.movie_id = cc.movie_id AND mi.movie_id = mi_idx.movie_id AND mi.movie_id = mk.movie_id AND mi.movie_id = cc.movie_id AND mi_idx.movie_id = mk.movie_id AND mi_idx.movie_id = cc.movie_id AND mk.movie_id = cc.movie_id AND n.id = ci.person_id AND it1.id = mi.info_type_id AND it2.id = mi_idx.info_type_id AND k.id = mk.keyword_id AND cct1.id = cc.subject_id AND cct2.id = cc.status_id;