SELECT MIN(t.title) AS typical_european_movie FROM postgres.company_type AS ct, postgres.info_type AS it, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.title AS t WHERE ct.kind = 'production companies' AND mc.note like '% (Canad%' and mc.note like '% (Canad%' AND mi.info IN ( 'Drama','Drama','Color','Drama','PFM:35 mm','RAT:1.33 : 1','Australia:MA15+','USA:29 March 2011','OFM:35 mm','OFM:35 mm' ) AND t.production_year > 1991 AND t.id = mi.movie_id AND t.id = mc.movie_id AND mc.movie_id = mi.movie_id AND ct.id = mc.company_type_id AND it.id = mi.info_type_id;