SELECT MIN(mc.note) AS production_note, MIN(t.title) AS movie_title, MIN(t.production_year) AS movie_year FROM postgres.company_type AS ct, postgres.info_type AS it, postgres.movie_companies AS mc, postgres.movie_info_idx AS mi_idx, postgres.title AS t WHERE ct.kind = 'production companies' AND it.info = 'votes distribution' AND mc.note not like '%oduction%' AND t.production_year between 1974 and 1995 AND ct.id = mc.company_type_id AND t.id = mc.movie_id AND t.id = mi_idx.movie_id AND mc.movie_id = mi_idx.movie_id AND it.id = mi_idx.info_type_id;