SELECT MIN(cn.name) AS company_name, MIN(lt.link) AS link_type, MIN(t.title) AS german_follow_up FROM postgres.company_name AS cn, postgres.company_type AS ct, postgres.keyword AS k, postgres.link_type AS lt, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.movie_keyword AS mk, postgres.movie_link AS ml, postgres.title AS t WHERE cn.country_code <> '[us]' AND ( cn.name LIKE '%%' OR cn.name LIKE '%%' ) AND ct.kind = 'production companies' AND k.keyword = 'church' AND lt.link LIKE '%r%' AND mc.note IS NULL AND mi.info IN ('RAT:16:9 HD','Drama','Norway:11','Short','Los Angeles, California, USA','English','Los Angeles, California, USA','Spain:21 May 2007') AND t.production_year BETWEEN 2002 and 2006 AND lt.id = ml.link_type_id AND ml.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND t.id = mc.movie_id AND mc.company_type_id = ct.id AND mc.company_id = cn.id AND mi.movie_id = t.id AND ml.movie_id = mk.movie_id AND ml.movie_id = mc.movie_id AND mk.movie_id = mc.movie_id AND ml.movie_id = mi.movie_id AND mk.movie_id = mi.movie_id AND mc.movie_id = mi.movie_id;