SELECT MIN(cn.name) AS company_name, MIN(lt.link) AS link_type, MIN(t.title) AS german_follow_up FROM postgres.company_name AS cn, postgres.company_type AS ct, postgres.keyword AS k, postgres.link_type AS lt, postgres.movie_companies AS mc, postgres.movie_info AS mi, postgres.movie_keyword AS mk, postgres.movie_link AS ml, postgres.title AS t WHERE cn.country_code <> '[jp]' AND ( cn.name LIKE '% F%' OR cn.name LIKE '% F%' ) AND ct.kind = 'production companies' AND k.keyword = 'dream' AND lt.link LIKE '%%' AND mc.note IS NULL AND mi.info IN ('Finland:K-12','USA:3 April 2010','Silent','USA','English','19','OFM:35 mm','USA:5 November 2005') AND t.production_year BETWEEN 1970 and 2006 AND lt.id = ml.link_type_id AND ml.movie_id = t.id AND t.id = mk.movie_id AND mk.keyword_id = k.id AND t.id = mc.movie_id AND mc.company_type_id = ct.id AND mc.company_id = cn.id AND mi.movie_id = t.id AND ml.movie_id = mk.movie_id AND ml.movie_id = mc.movie_id AND mk.movie_id = mc.movie_id AND ml.movie_id = mi.movie_id AND mk.movie_id = mi.movie_id AND mc.movie_id = mi.movie_id;